			final BusinessEntity entity = (BusinessEntity) mData.get(position);
			holder.businessName.setText(entity.getBusinessName());
			Glide
					.with(mContext)
					.load(entity.getBusinessImage())
					.centerCrop()
					.placeholder(R.mipmap.ic_launcher)
					.crossFade()
					.into(holder.businessImg);


			// 关键代码
