			PermissionInfo[] definedPermissions=myPackageInfo.permissions;
			for(int i=0;i<definedPermissions.length;i++){
				int protLevelReportedBySystem = pm.getPermissionInfo(definedPermissions[i].name,0).protectionLevel;
