            uint amount; // æŠ•èµ„é‡‘é¢
        }
        // é‡‡ç”¨ç»“æ„ä½“æ¥æè¿°ä¼—ç­¹äº§å“
        struct Product{
            address addr; // å¦‚æœä¼—ç­¹æˆåŠŸ,åˆ™é‡‘é¢ä¼šè½¬åˆ°å½“å‰åœ°å€
            uint goal; // é¢„æœŸä¼—ç­¹çš„ç›®æ ‡,å¦‚æœè¾¾åˆ°æ­¤ç›®æ ‡åˆ™è¯´æ˜ä¼—ç­¹æˆåŠŸ
            uint amount; // å®é™…ä¼—ç­¹çš„é‡‘é¢
            uint funderNum; // ç»Ÿè®¡æŠ•èµ„è€…çš„äººæ•°,ç¼ºçœå€¼ä¸º0
            // æ˜ å°„ç±»å‹,ç»Ÿè®¡å½“å‰äº§å“çš„æŠ•èµ„è€…
            mapping(uint => Funder) funders;
        }

         // å¹³å°è¦ç»Ÿè®¡ä¼—ç­¹çš„äº§å“æ•°é‡
        uint count;
         // æ­¤æ˜ å°„ä¸»è¦è®°å½•å¹³å°çš„ä¼—ç­¹äº§å“
        mapping (uint => Product) public products;
        // æ·»åŠ ä¼—ç­¹äº§å“ä¿¡æ¯
        function candidate(address addr, uint goal) returns (uint compaingnID){
            // ç»“æ„ä½“æ˜¯ä¸éœ€è¦new,æ­¤å¤„æŒ‰ç…§ç»“æ„ä½“å£°æ˜çš„å˜é‡é¡ºåºè¿›è¡Œèµ‹å€¼
            products[count++] = Product(addr,goal*10**18,0,0);
        }

         // æ­¤å‡½æ•°å®ç°å¯¹äº§å“è¿›è¡Œä¼—ç­¹åŠŸèƒ½
        function vote(uint index) payable {
            // é€šè¿‡ç´¢å¼•è·å–è¦ä¼—ç­¹äº§å“ä¿¡æ¯
            Product p = products[index];
            // åˆ›å»ºæŠ•èµ„è€…,å¹¶ä¸”å­˜å‚¨åˆ°äº§å“ä¼—ç­¹æ˜ å°„ä¸­
            // msg.sender:å½“å‰å‡½æ•°è°ƒç”¨è€…,å°±æ˜¯ä¼—ç­¹è€…, msg.valueï¼šä¼—ç­¹é‡‘é¢æ˜¯è°ƒç”¨å‡½æ•°æ—¶ä¼ å…¥çš„valueå€¼
            p.funders[p.funderNum++] = Funder({addr:msg.sender,amount:msg.value});
            // æŠŠå½“å‰ä¼—ç­¹é‡‘é¢è¿½åŠ åˆ°amountä¸­
            p.amount += msg.value;
        }

        // æ£€æµ‹å½“å‰äº§å“ä¼—ç­¹æ˜¯å¦æˆåŠŸ(å¦‚æœæˆåŠŸåˆ™ä¼—ç­¹é‡‘é¢è½¬åˆ°äº§å“æä¾›çš„åœ°å€)
        function check(uint index) payable returns (bool) {
            Product p = products[index];
            // åˆ¤æ–­å½“å‰ä¼—ç­¹é‡‘é¢æ˜¯å¦å¤§äºè®¾ç½®é‡‘é¢
            if (p.amount < p.goal) {
                return false;
            }
            // ä¼—ç­¹æˆåŠŸ,å½“å‰é‡‘é¢è¦è½¬ç»™äº§å“åœ°å€
            uint amount = p.amount;
            // åˆå§‹åŒ–amount
            p.amount = 0;
            p.addr.transfer(amount); // å¦‚æœå¤±è´¥åˆ™è¿”å›ä¸ºfalse
            // transfer equal send 
            // if(!p.addr.send(amount)){
            //     throw;
            // }
            return true;
        }


    }

